package demoAssignmentProject;

public class DemoAssignmentProject {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0;i<10;i++)
			if (i%2==0)
		{
		System.out.println(i + " This is an even number");
		}
		
		// Reverse
		for(int i=10;i>=1;i--)
			if (i%2==0)
		{
		System.out.println(i + " This is an even number");
		}
		

	}

}
